CREATE TABLE `cars` (
  `id` int(11) NOT NULL,
  `brand_id` int(11) NOT NULL,
  `model` varchar(50) NOT NULL,
  `text` text NOT NULL,
  `icon` varchar(255) NOT NULL,
  `img` varchar(255) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `cars`
--

INSERT INTO `cars` (`id`, `brand_id`, `model`, `text`, `icon`, `img`) VALUES
(1, 1, 'F103', '<p><b>F103</b> &mdash; это название серии среднеразмерных автомобилей, выпускавшихся Auto Union в Западной Германии с 1965 по 1972 год. Серия являлась преемником DKW F102. Чтобы подчеркнуть переход от двухтактных двигателей к четырёхтактным, марка DKW была упразднена в пользу Audi.</p> ', '03505076b73dadf5facf36921a0e1cba.png', '24a845da5eb4f2fd6d2998f3bc2b74a5.JPG'),
(2, 1, 'RS5', '<p>Спортивное купе <b>Audi RS5</b> является наиболее быстрым и дорогостоящим авто в семействе &laquo;Ауди А5&raquo;. Его фейслифтинг затронул экстерьер модели.  Бамперы остались прежними. Клиренс &laquo;Ауди Эр Эс 5&raquo;, по сравнению с &laquo;а-пятой&raquo;, уменьшен на 20 мм. Теперь он составляет 12 см. Эластокинематика подвесок модели улучшена, активные амортизаторы перенастроены.  Что касается ходовой части автомобиля 2012 года, то она не была подвержена модернизации. Высокофорсированная алюминиевая &laquo;восьмёрка&raquo; оснащается системой непосредственного впрыска топлива. При рабочем объёме 4.2 литра она выдаёт 450 л. с. Мотор Audi RS5 сопряжён с механизмом технологии старт/стоп. Тяга на оси передаётся через роботизированную коробку передач S tronic с двумя сцеплениями. Достигая сотни спустя 4.5 секунды после старта, &laquo;эр-эска&raquo; едва ли даст опередить себя спортивному купе BMW M3 с менее мощным агрегатом.  У более дорогой модели Alpina B3 технические характеристики не дотягивают до возможностей &laquo;Ауди&raquo;. Всего 360 &laquo;лошадей&raquo; трудятся под капотом баварского суперкара.</p>', '4b38a75e2115d82d876c3c67c14e5900.png', '260b9bc7a309e32209aaaefa937a9fdc.jpg'),
(3, 1, 'RS7', '<p><b>Audi RS7</b> оснащается 4,0-литровым бензиновым двигателем V8. Мощность твин-турбо мотора составляет 605 л.с. и 700 Нм крутящего момента. Ранее отдача агрегата не превышала 560 лошадиных сил. До 100 км/ч модель разгоняется за 3,7 секунды. Средний расход топлива составляет 9,5 л на 100 км пути.    RS7 оснащается модернизированной 8-ступенчатой автоматической коробкой передач Tiptronic, системой полного привода, центральным дифференциалом и адаптивной пневмоподвеской.</p>', '294a9a73a533095a88697cb96c04b924.png', '5b9f4ade15c441bf17e018f1929b0120.jpg'),
(4, 1, 'R8', '<p><b>Audi R8</b> &mdash; среднемоторный полноприводный спорткар, производимый немецким автопроизводителем Audi с 2007 года. Впервые представлен в 2006 году на Парижском автосалоне.  Модель R8, как и ряд других моделей автоконцерна Audi производится исключительно на заводе Audi Sport GmbH (до ноября 2016 г. &quot;Quattro GmbH&quot;)</p>', '131ac2463dbba93b0e09c62b866e388c.png', '6b3bb7f5f94cd82a1f3ac73a278c9317.jpg'),
(5, 1, '920', '<p><b>AUDI 920</b> был создан в 1938 чтобы заменить Audi Front UW 225. Двигатель был сокращенной версией восьмицилиндрового встроенного двигателя компании Horch. Автомобиль планировался, чтобы занять нишу в Объединении Авто в продукции Horch и автомобилями среднего рынка. AUDI не имел никаких автономных производственных средств в то время, и автомобиль производился, подобно предшественнику, на заводе Horch.</p>', '51733eb77b7bc2a8ec9515b6aa7ccf4f.png', '9d74d344008e0ceda9f53334a213dc5d.jpg'),
(6, 2, '3200', '<p><b>BMW 3200 CS</b> &mdash; спортивное купе от BMW, выпускавшееся с февраля 1962 по сентябрь 1965 года совместно с кузовным ателье Bertone. Всего было собрано около 600 машин этой модели. Технической основой машины была модель BMW 502.  3200 CS стала последней восьмицилиндровой моделью BMW своей эпохи, следующий BMW с V8 появился только в 1992 году.  Эта модель стала первым автомобилем марки с так называемым изгибом Хофмайстера (изгибом передней части задней стойки крыши) &mdash; оригинальным стилистическим решением дизайнеров. Изгиб Хофмайстера, наряду с парной решёткой облицовки радиатора, до сих пор является характерным для автомобилей BMW.</p>', '880aa1f6e4fd96cd477160f3a0519437.png', '9509df7fca6f2140ef6f699613929c2e.jpg'),
(7, 2, '503', '<p><b>BMW 503</b> &mdash; двухдверный автомобиль класса Gran Turismo, который был представлен на IAA в сентябре 1955 года, вместе с родстером BMW 507. Дизайнер Альбрехт Гёрц считал 503-й своей самой успешной работой.  Производство началось в мае 1956 года, первоначальная цена составляла 29 тыс. немецких марок. Трансмиссия и рулевое управление были взяты от BMW 502. Двигатель устанавливался бензиновый V-образный 8-цилиндровый, полностью легкосплавный, с мокрыми гильзами, верхним распределительным валом и двухкамерным карбюратором Zenith. Он выдавал 140 л. с. на 4 800 оборотах и 216 Нм крутящего момента на 3 800 оборотах. Как и 507-й, 503-й был исключительно двухместный, но отличался иными передней и задней частью. Кабриолет 503-й модели первым из европейских автомобилей получил электропривод крыши. Кроме кабриолета, было доступно купе с виниловой крышей.  Как и 507-я, модель 503 была провальной. Производство завершилось в марте 1959 года. Наследник, BMW 3200 CS, был длиннее и имел четыре полноценных места.</p>', '967bf5568bf24d2ce0d111bac5cedba0.png', '71e06a0e3c3a320a9bc525f7e4457ebb.jpg'),
(8, 2, '6ER', '<p><b>BMW 6</b> или шестая серия BMW &mdash; автомобили серии купе бизнес-класса. Производство данной модели началось с 1976 года. Основным конкурентом для этой серии являются автомобили семейства Jaguar XK.</p>\r\n<p>Шестая серия БМВ оборудована бензиновыми двигателями: 3.0 R6 24V 272 л.с. 320 Нм (630i), 4,4 V8 32V 333 Л.с.(645i), 4,8 V8 32V 490 Нм (650i) и трёхрежимным гоночным 5.0 V10 507 л.с. 520 Нм для М6 и турбодизельным 3.0 R6 24V с 2 турбонагнетателями 286 л.с. 580 Нм (635d в РФ не продаётся).</p>', 'e03c33f9525c344e800d1c4b3894873b.png', '87ae8d9d7919ff7b350a79c6d31f5b43.jpg'),
(9, 2, 'E9', '<p>Модель CS существовала в кузове купе на базе стандартного седана, и выглядела идентично спереди. Но E9 также связана с ранней 2000CS, что видно сзади. Первая модель была хорошо оборудованная 2800CS 1968 года. Модели 3.0CS появились в 1971 году. В 1974 году похожая 2.5CS производилась в небольшом количестве, в ответ на Нефтяной кризис 1973 года. Модели CSL были легкими гоночными версиями, которые соревновались в Группе 2 на чемпионате European Touring Car Championship. На некоторых соревнованиях автомобиль с некоторым успехом конкурировал против гоночных версий Porsche 911 и Ford Capri. Со всеми аэродинамическими спойлерами для гонок, автомобиль стал известен как &laquo;Бэтмобиль&raquo;.</p>', '3c08dbe221252df92cf908903e0cdb42.png', '19c1f307ddb72cf58290db65322fae81.jpg'),
(10, 2, 'M6', '<p><b>BMW M6</b> &mdash; высокотехнологичная версия BMW 6-ой серии, разработанная спортивным подразделением немецкого автопроизводителя BMW. Первое поколение было представлено в 1983 году. BMW M6 очень богата электроникой, имеет от 5 до 12 режимов передач.  Существуют следующие поколения М6:  BMW M635CSi / M6 E24 (1986&mdash;1989) BMW М6 E63/E64 (2005 - 2010) BMW M6 F12/F13 (с 2012)</p> ', '103a1100d2376e3c4d9b81c9cf3ba7fa.png', '94d465f2066c24c84fa90bef90f8d0ec.jpg'),
(11, 2, 'I8', '<p>BMW Vision Efficient Dynamics, <b>BMW i8</b> &mdash; автомобиль компании BMW. Концепт-кар был представлен в 2009 году, первый серийный образец &mdash; на Франкфуртском автосалоне в сентябре 2013 года.  BMW Vision Efficient Dynamics представляет собой полноприводное двухдверное купе. Шасси и подвеска сделаны из алюминия, крыша и двери выполнены из поликарбоната. Коэффициент аэродинамического сопротивления 0,26.  Приборная панель расположена на центральном Head-up дисплее и лобовом стекле. Такое сочетание создаёт впечатление трёхмерного пространства. Наиболее важная информация, такая как показание спидометра и маршрут следования, выдаётся на лобовое стекло в фоновом виде, что обеспечивает наибольшую безопасность, так как водителю не приходится смотреть на панель приборов.  Силовой установкой автомобиля является гибридная система Plug-in hybrid. Система использует сочетание 2-х электродвигателей, питающихся от литий-ионных аккумуляторов и 1,5-литрового трехцилиндрового бензинового двигателя с турбонаддувом B38K15T0 (англ.)русск. мощностью 170 кВт (231 л.с.). Первый электродвигатель располагается на передней оси и выдаёт постоянную мощность 60 кВт, и максимальную 96 кВт. Второй электродвигатель расположен между основным электродвигателем и бензиновым двигателем, выдаёт постоянную мощность 25 кВт и служит для запуска ДВС. Трансмиссия представляет собой 6-и ступенчатую роботизированную коробку передач второго поколения.  В общей сложности автомобиль развивает мощность 266 кВт (362 л.с.) и максимальный крутящий момент 570 Нм. Разгоняется до 100 км/ч за 4,4 секунд и развивает максимальную скорость 250 км/ч (ограничена электроникой). Расход топлива в смешанном цикле составляет 2,1 литра на 100 км. Запас хода от аккумуляторных батарей в среднем около 30 км. Максимальный общий запас хода составляет 600 км.</p>', '8e0f36ae9893ef19214cec7d46eadb88.png', 'c60646a599273d1dd15c06f3d4293185.jpg'),
(12, 3, 'DB5', '<p><b>Aston Martin DB5</b> &mdash; автомобиль класса GT, выпущенный в 1963 году компанией Aston Martin, в качестве продолжения серии DB4. Аббревиатура &laquo;DB&raquo; происходит от инициалов David Brown (рус. Дэвид Браун), владельца компании Aston Martin в 1947&mdash;1972 годах. DB5 известна как наиболее узнаваемая машина Джеймса Бонда. В фильмах автомобиль появляется всегда с одним из регистрационных знаков BMT216A и BMT214A. Всего был выпущен 1021 автомобиль Aston Martin DB5.</p>\r\n<p>Основными отличиями от DB4 стали: увеличение объёма алюминиевого двигателя с 3,7 л. до 4.0 л., новая 5-ступенчатая трансмиссия ZF (за исключением некоторых, самых первых автомобилей), и 3 Skinners Union карбюратора. В итоге DB5 имела 282 л. с. и могла развивать скорость до 233 км/ч (145 миль/ч). Двигатель объёмом 4.0 л. был доступен на DB4 Vantage с марта 1962 года, и стал стандартным для Aston Martin c выходом DB5 в сентябре 1963 года.</p> ', '5ba90e1dbd43dcc83f76ab66fe6d9a6b.png', '50c3a15d5ac17ead1317d096e6d0cec7.jpg'),
(13, 3, 'DB9', '<p><b>Aston Martin DB9</b> &mdash; автомобиль класса Gran Turismo, который производится Aston Martin с 2004 года. Доступен в двух кузовных вариантах: купе и кабриолет. Модель была впервые представлена в 2003 году на Франкфуртском автосалоне на замену DB7, дизайнерами автомобиля были Ян Каллум (англ. Ian Callum) и его преемник Генрик Фискер (англ. Henrik Fisker), который и закончил разработку. DB9 стал первым автомобилем, предназначенный для сборки на фабрике Aston Martin в Гэйдоне. DB9 в основном состоит из алюминия, как и на DBS шасси - VH platform, двигатель, тот же что и на Aston Martin V12 Vanquish - 6-литровый V12. Разгоняется до 100 км/ч (62 миль/ч.) за 4.6 с., максимальная скорость 295 км/ч. (183 миль/ч.)</p>\r\n<p>В отличие от Aston Martin DBS, модель DB9 оснащается немного менее мощным двигателем V12 объемом 6л. Максимальная мощность данного силового агрегата достигается при 6000 об/мин и равна 477 л.с., максимальный крутящий момент &ndash; 600 Н&middot;м уже при 5000 об/мин. Ещё один показатель этого автомобиля, который лишний раз доказывает, что это поистине мощный спорткар &ndash; разгон с 0 до 100 км/ч, у DB9 этот показатель равен 4.8 секунды на механической коробке передач и 4.5 секунды на АКПП &laquo;Touchtronic&raquo;. Максимальная скорость при этом зафиксирована на уровне 306 км/час.</p>', '97d5f234ee821a2b3dd706edf9d03186.png', '911d0f822ca6321c0865f3a33b982991.jpg'),
(14, 3, 'RAPIDE', '<p><b>Aston Martin Rapide</b> &mdash; 4-дверное спортивное купе с переднемоторной компоновкой производства британской компании Aston Martin на модифицированной платформе модели Aston Martin DB9. В 2018 году автомобиль будет снят с производства. Вместо него на рынке появятся новые модели DBX, Lagonda, а также электрокар.</p>', '7ea1a25ce6ad8bf05a9880e081d6ddcd.png', '3029df8902af948a33a71d374bd0db08.jpg'),
(15, 3, 'V12 VANQUISH', '<p><b>Aston Martin Vanquish</b> является флагманским спорт-каром класса Gran Turismo компании Aston Martin. Был создан в 2001-м году в качестве преемника стареющего Virage. В 2007 году был заменен на Aston Martin DBS, но после окончания производства DBS, вновь вернули название Vanquish.</p>', '56e0feb8f1e93bb355e7ea5ffd6396f1.png', '74ac8965b8c9b3cfd34bd00f9500ec66.jpg'),
(16, 3, 'V12 VANTAGE', '<p><b>Aston Martin V8/V12 Vantage</b> &mdash; спортивный автомобиль с кузовом из алюминия и композитных материалов на платформе V/H. Премьера состоялась в марте 2005 года в Женеве, а производство началось с декабря 2005 года. Трансмиссия создана по схеме transaxle, 6-ступенчатая МКП с вынесенной к задней оси главной передачей, дифференциал ограниченного трения. Ручная сборка, комплектация по индивидуальному заказу. Приблизительная цена: 150 000 евро.</p>', '520111ffaa7ec9816fd422053f4af14d.png', '81bf90eefa44f44659ffff531ee8029c.jpg'),
(17, 3, 'VIRAGE', '<p>Впервые <b>Aston Martin Virage</b> в кузове купе был представлен публике в рамках международного автосалона, проходившего в 1988 году в Бирмингеме. Автомобиль пришел на смену модели V8, которая на тот момент выпускалась уже порядка 20 лет. В наследство от предшественника первый Астон Мартин Вираж получил 8-цилиндровый двигатель, объемом 5,3 л и мощностью 330 л.с. При весе в 1,7 тонны он разгонялся до 100 км/ч примерно за 6,5 секунд, а максимальная скорость была ограничена 254 км/ч. Примечательно, что за время существования модели с 1989 по 1993 год было выпущено не более 50 Aston Martin Virage в кузове купе с 5-скоростной механикой ZF. Все остальные машины оснащались 3-ступенчатой АКПП Chrysler. Для автомобиля люксового сегмента он позаимствовал немало комплектующих у серийных машин других марок. В том числе переднюю оптику от Audi 200, заднюю от VW Scirocco, панель управления климат-контролем от Jaguar и многое другое. В результате в 1993 году на конвейере Астон Мартин Вираж сменил Vantage, который, по сути, стал вторым поколением спорт-купе и выпускался до 2000 года. Возрождение модели произошло весной 2011 года, когда новый Aston Martin Virage дебютировал на Женевском автосалоне, заняв в модельном ряду место между DB9 и флагманом DBS. В основе GT лежит фирменный пространственный каркас VH, а в качестве единственного силового агрегата покупателям предлагается 6-литровый V12, мощностью 497 л.с., который агрегируется с 6-ступенчатым &quot;автоматом&quot; ZF.Несмотря на то, что Астон Мартин Вираж имеет переднемоторную компоновку, коробка передач смещена к задней оси, благодаря чему конструкторам удалось достичь идеальной развесовки по осям 50:50. Еще одной особенностью модели стали карбоно-керамические тормоза, композитный карданный вал, имеющий малый вес, а также адаптивная подвеска. Амортизаторы Aston Martin Virage автоматически выбирают один из пяти уровней жесткости для различных режимов езды. Остается добавить, что он стал победителем в конкурсе &quot;Классический автомобиль будущего&quot;, по версии немецкого автомобильного журнала Auto Bild Klassik.</p>', '632bc8a661548bc785cd96eeda043ee7.png', '5cd091162b8eb1a90527f379a6cc98ca.jpg'),
(18, 4, '458', '<p><b>Ferrari 458 Italia</b> &mdash; суперкар, представленный в 2009 году на Франкфуртском автосалоне. Модельный год среднемоторного купе заявлен как 2010, производство и продажи стартовали в 2009 году. По данным на начало сентября 2010 года продано около 1 200 экземпляров.</p>', 'b11d3840e39e61370d86e95e381df285.png', '6da3bfa858a369dcebbb997e94260c70.jpg'),
(19, 4, 'CALIFORNIA', '<p><b>Ferrari California</b> &mdash; 2-х местный родстер с жесткой раскладывающейся крышей итальянской компании Ferrari. Автомобиль назвали в честь Ferrari 250 GT California Spyder.</p>', '3e2fb8443aa81bed0ca6518909ddfe9b.png', 'f8e7d5a955b63b511a5db20daaa8d9cd.jpg'),
(20, 4, 'F12BERLINETTA', '<p><b>Ferrari F12berlinetta</b> (также называется F12 Berlinetta) &mdash; гран туризмо с передним расположением двигателя и задним приводом от итальянского производителя спортивных автомобилей Ferrari. F12berlinetta впервые представили в 2012 году на Женевском автосалоне. В модельном ряду среди автомобилей гран туризмо заменил Ferrari 599 серии.</p>', '85bc3e3429ab4c33789dec3954d1db61.png', '3920bcfc9df9ca1a36db8fcdad982e1c.jpg'),
(21, 4, 'FF', '<p><b>Ferrari FF</b> (FF означает &quot;Ferrari Four&quot;, то есть четыре места и четыре ведущих колеса) &ndash; автомобиль класса Гран Туризмо, показанный Ferrari 21 января 2011 года. Его официальная презентация была проведена 1 марта 2011 года на Женевском автосалоне. В этой модели есть две принципиально новые для автомобилей, выпускаемых в Маранелло особенности &ndash; это первый полноприводник Ferrari, и её первый суперкар в кузове хетчбэк. Его разработчики из кузовного ателье Carrozzeria Pininfarina S.p.A. выбрали для трехдверного полноприводного суперкара редкую в настоящее время модификацию универсала под названием Shooting Break (дословный перевод &ndash; &laquo;охотничий привал&raquo;). Такой кузов обеспечивает удобство трансформации салона при стильном внешнем виде.  Ferrari FF пришёл на смену Ferrari 612 Scaglietti в модельном ряду компании. Максимальная скорость FF составляет 335 км/ч, с места до 100 км/ч машина разгоняется за 3,7 секунды. Ferrari позиционирует FF как самый быстрый полноприводный автомобиль в мире. Цена FF составляет 300 000 USD.  Самим именем модели, FF, Ferrari ясно дает понять, что этот автомобиль более практичен, чем его предшественник, 612 Scaglietti. Его система полного привода позволяет увереннее вести машину в сложных погодных условиях, таких как дождь и снег, а четыре места уже достаточно, чтобы считать FF утилитарным автомобилем.</p>', 'fe36d5c44ce78842b310a6d1eb052b77.png', '12e2c139260a29c0d2a888fd8961efae.jpg'),
(22, 4, 'FXX K', '<p><b>Ferrari FXX K</b> - это гоночный автомобиль с высокой производительностью (40 единиц), изготовленный автомобильным производителем Ferrari в Маранелло, Италия и спроектированный Марко Файнелло и архитектором Флавио Манцони, директором Centro Stile Ferrari. FXX K основан на уличном юридическом LaFerrari. Это преемник FXX и 599XX.</p>', '6ae85580aa29a056426f0c7c99a7bb95.png', 'a5c7403973ce8d76df4f6c25e5a38043.jpg'),
(23, 4, 'GTC4LUSSO', '<p><b>Ferrari GTC4Lusso</b> - четырехместный большой турер, выпускаемый итальянским производителем автомобилей Ferrari. Ferrari GTC4Lusso является преемником Ferrari FF, который имеет аналогичный стиль кузова для GTC4Lusso.</p>', '3f8ddc6abc9b2adc1d230a240c5cdccf.png', '526da3d56e3b227872c8129c583b54f9.jpg'),
(24, 4, 'LAFERRARI', '<p><b>LaFerrari</b> (также известен как Ferrari F70/F150) &mdash; гибридный гиперкар итальянской фирмы Ferrari, первый серийный гибридный автомобиль компании. Выпущен ограниченной серией в 2013 году.  Свои намерения выпустить преемника знаменитого Ferrari Enzo фирма впервые высказала ещё в марте 2007 года. В сентябре стала известна примерная цена и мощность автомобиля. В конце 2009 года компания снова опубликовала данные о суперкаре, в 2010 также напомнив о желании запустить в серию нового флагмана. В 2011 итальянская марка заявила, что автомобиль будет гибридным, а на следующий год стало известно о системе рекуперации HY-KERS, впервые применённую на автомобилях марки. Затем, в апреле, на автосалоне в Пекине компания представила воочию саму гибридную установку. В июне появились шпионские фотографии версии в камуфляже, в декабре Ferrari показали тизер автомобиля. На Женевском автосалоне 2013 года готовая версия суперкара была наконец представлена, также только на самом моторшоу появилось официальное имя самого мощного купе компании. Цена автомобиля превышает 1,3 миллиона евро, всего произведено 499 экземпляров. Специально произведённый для благотворительности 500-й LaFerrari был продан в декабре 2016 года за 7 миллионов долларов. Все средства направлены на помощь жертвам серии землетрясений, которые произошли осенью 2016 года в Италии. Данный автомобиль стал самым дорогим из выпущенных в XXI веке. В 2017 году выйдет новая модель &mdash; Ferrari Laferrari Spider.</p>', '7b4d000070063d092ec18e9764a41953.png', '052caca0e2b531683b3f2fdb1ff50d96.jpg'),
(25, 1, 'A4', '<p><b>Audi 80</b>, с 1995 года Audi A4, представляет собой семейство моделей среднего класса, выпускаемых под маркой Audi. Внутреннее обозначение автомобилей этой серии &mdash; &laquo;тип В&raquo;.</p>\r\n\r\n<p>Audi 80 стал преемником серии Audi F103, модели которой &mdash; Audi 60, 75, 80 и Super 90&mdash; обозначались по числу лошадиных сил. Наименование Audi 80 стало использоваться для всего модельного ряда и в 1994 году было заменено на A4.</p>', '7606a828677d9df68cea7e1b8d3ab307.png', 'bb864a723db25defbad1d163df3a92d9.jpg'),
(26, 2, '3er', '<p><b>BMW 3</b>, или BMW третьей серии, &mdash; компактный автомобиль, выпускающийся немецким премиум-автопроизводителем BMW AG. Начиная с 1975 года было выпущено 6 поколений модели. На нынешний момент выпускаются автомобили в кузовах F30 (седан), F31 (универсал), F34 (хэтчбэк). Так же начиная со второго поколения модели выпускается специальная спортивная версия автомобиля, именуемая BMW M3, доработанная заводским отделением тюнинга BMW &mdash; BMW M. BMW 3-й серии является самой продаваемой моделью компании &mdash; в 2005 году около 40% продаж BMW AG пришлись на 3-ю серию. Модели серии периодически попадают в список самых продаваемых автомобилей в Европе.</p>', '2ae0a4f26a02e8072aff83077c2f5504.png', '2d93bea2c4f7931f6b24d57ea91095a4.jpg'),
(27, 5, '190 SL', '<p>В 1954 году компания&nbsp;<strong>Mercedes-Benz</strong>&nbsp;на международной автовыставке в&nbsp;Нью-Йорке&nbsp;представила новый автомобиль <strong>Mercedes-Benz 190 SL</strong>. Данная модель представляла привлекательную, более доступную альтернативу Mercedes-Benz 300 SL, разделяя её основные идеи и технологические особенности. Сконструирован новый автомобиль был на базе R121 платформы (модификация седана&nbsp;W121). Мощность автомобиля составляла 77&nbsp;кВт&nbsp;(105 л.с. при 5700 об/мин и 142 Нм при 3200 об/мин)</p>\r\n\r\n<p>Автомобиль был доступен в двух вариантах: кабриолет с мягким верхом (первоначально по цене&nbsp;16&nbsp;500&nbsp;немецких марок / $3998) или съёмной жёсткой крышей с (17,650 немецких марок / $4295).</p>\r\n\r\n<p>В период с 1955 по 1963 год было выпущено&nbsp;25&nbsp;881&nbsp;таких автомобилей. В 1963 году и 190SL и 300SL были заменены на Mercedes-Benz 230SL (W113).</p>\r\n', '5ba90e1dbd43dcc83f76ab66fe6d9a6b.png', '3a7c6923722044b16fe0119eddc60274.jpg'),
(30, 1, 'A6', '<p><strong>Audi 100</strong>, с&nbsp;1994 года&nbsp;<strong>Audi A6</strong>,&nbsp;&mdash; семейство автомобилей&nbsp;бизнес-класса, выпускающихся под маркой&nbsp;Audi, внутреннее обозначение&nbsp;&mdash; &laquo;тип C&raquo;. Премьера&nbsp;Audi 100&nbsp;состоялась в&nbsp;1968&nbsp;году, с&nbsp;августа 1994 года этот модельный ряд носит название А6.</p>\r\n\r\n<p>В&nbsp;отличие от&nbsp;Audi 80/A4, где переименование совпало с&nbsp;выпуском новой модели, первый А6&nbsp;представлял собой рестайлинговую версию последнего&nbsp;Audi 100.</p>\r\n\r\n<p>Сегодня Audi A6&nbsp;предлагается в&nbsp;четырехдверной версии с&nbsp;кузовом седан и&nbsp;в&nbsp;пятидверной версии Avant (универсал), ранее выпускались также двухдверные модели, купе и&nbsp;хэтчбек. Спортивная модификация А6&nbsp;производится под наименованием&nbsp;Audi S6, а наиболее мощная версия A6 производится под наименованием&nbsp;RS6, что расшифровывается как&nbsp;&quot;RennSport&quot;.</p>\r\n', '578f988eefebd879e61e13c22f30563a.png', '673642942dadca563f9dded788abb7f6.jpg');

